import {Component, inject, OnDestroy, OnInit} from '@angular/core';
import {AuthService} from "../Services/auth.service";
import {User} from "../Model/user";
import {Subscription} from "rxjs";
import {Router} from "@angular/router";

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit, OnDestroy{

  authService: AuthService = inject(AuthService);
  router: Router = inject(Router);

  isLoggedIn = false;

  private userSubscription: Subscription;

  ngOnInit() {
    this.userSubscription = this.authService.user.subscribe((user: User) => {
      this.isLoggedIn = user ? true : false;
    })
  }

  ngOnDestroy() {
    this.userSubscription.unsubscribe();
  }

  onLogOut() {
    this.authService.logOut();
    this.router.navigate(['/login']);
    localStorage.clear();
  }

}
<div class="app-header">
    <!--APP LOGO-->
    <div class="app-header-logo">
        <h2>Task Management</h2>
    </div>

    <!--APP NAVBAR-->
    <div class="app-header-navbar">
        <a routerLink="/">HOME</a>

        <div class="dropdown" *ngIf="isLoggedIn">
            <button class="dropbtn">DASHBOARD
                <i class="fa fa-caret-down"></i>
            </button>
            <div class="dropdown-content">
                <a routerLink="dashboard/overview">OVERVIEW</a>
                <a routerLink="dashboard/stats">STATS</a>
            </div>
        </div>

        <a routerLink="/login" *ngIf="!isLoggedIn">LOGIN</a>
        <a *ngIf="isLoggedIn" (click)="onLogOut()">LOGOUT</a>
    </div>
</div>
