import {Component, inject, OnInit} from '@angular/core';
import {FormBuilder, FormGroup} from "@angular/forms";

@Component({
    selector: 'app-login',
    templateUrl: './login.component.html',
    styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{

    loginForm: FormGroup = new FormGroup({});

    isLoginMode = true;

    constructor(
        private formBuilder: FormBuilder
    ) {
    }

    onSwitchMode() {
        this.isLoginMode = !this.isLoginMode;
    }

    ngOnInit() {
      this.buildForm();
    }

    buildForm() {
      this.loginForm = this.formBuilder.group({
        email: undefined,
        password: undefined
      });
    }

    onSubmit() {
        console.log(this.loginForm.value);
    }

}
<div class="app-login-form">
    <h2>{{ isLoginMode ? 'Login Here' : 'Sign Up Here' }}</h2>
    <form [formGroup]="loginForm">
        <div class="form-input">
            <label>Email Address</label>
            <input type="email" placeholder="Email address">
        </div>
        <div class="form-input">
            <label>Password</label>
            <input type="password" placeholder="Password"
                   minlength="8"
            >
        </div>
        <button type="submit" class="app-login-btn" (click)="onSubmit()">
            {{ isLoginMode ? 'Login' : 'Sign Up' }}
        </button>
        <p>{{ isLoginMode ? 'Do not have an accout yet ?' : 'Already Have an account' }}
            <button class="app-toggle-btn" (click)="onSwitchMode()">
                {{ isLoginMode ? 'Register Here.' : 'Sign Up Here' }}
            </button>
        </p>
    </form>
</div>
