import {Component, inject, OnInit} from '@angular/core';
import {FormBuilder, FormGroup} from "@angular/forms";

@Component({
    selector: 'app-login',
    templateUrl: './login.component.html',
    styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{

    loginForm: FormGroup = new FormGroup<any>({});

    isLoginMode = true;

    formBuilder: FormBuilder = inject(FormBuilder);

    onSwitchMode() {
        this.isLoginMode = !this.isLoginMode;
    }

    ngOnInit() {
      this.buildForm();
    }

    buildForm() {
      this.loginForm = this.formBuilder.group({
        email: undefined,
        password: undefined
      });
    }

}
<div class="app-login-form" [formGroup]="loginForm">
    <h2>{{ isLoginMode ? 'Login Here' : 'Sign Up Here' }}</h2>
    <form>
        <div class="form-input">
            <label>Email Address</label>
            <input type="email" placeholder="Email address">
        </div>
        <div class="form-input">
            <label>Password</label>
            <input type="password" placeholder="Password">
        </div>           
        <button type="submit" class="app-login-btn">{{ isLoginMode ? 'Login' : 'Sign Up' }}</button>
        <p>Do not have an accout yet?
            <button class="app-toggle-btn" (click)="onSwitchMode()">Register Here.</button>
        </p>
    </form>
</div>
