import {Component, inject, OnInit} from '@angular/core';
import {FormBuilder, FormGroup} from "@angular/forms";
import {AuthService} from "../Services/auth.service";

@Component({
    selector: 'app-login',
    templateUrl: './login.component.html',
    styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{

    loginForm: FormGroup = new FormGroup({});

    isLoginMode = true;
    isLoading = false;

    authService: AuthService = inject(AuthService);

    constructor(
        private formBuilder: FormBuilder
    ) {
    }

    onSwitchMode() {
        this.isLoginMode = !this.isLoginMode;
    }

    ngOnInit() {
      this.buildForm();
    }

    buildForm() {
      this.loginForm = this.formBuilder.group({
        email: '',
        password: ''
      });
    }

    onSubmit() {
        this.isLoading = true;
        const email = this.loginForm.get('email').value;
        const password = this.loginForm.get('password').value;
        console.log(this.loginForm.value);
        if (this.isLoginMode) {

        } else {
            this.authService.signUp(email, password).subscribe({
                next: (res) => {
                    console.log('res', res);
                    this.isLoading = false;
                },
                error: (err) => {
                    console.log('err', err);
                    this.isLoading = false;
                }
            })
        }
    }

}
<div class="app-login-form">
    <h2>{{ isLoginMode ? 'Login Here' : 'Sign Up Here' }}</h2>
    <app-loader *ngIf="isLoading"></app-loader>
    <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">
        <div class="form-input">
            <label>Email Address</label>
            <input type="email" placeholder="Email address"
                   formControlName="email" required
            >
        </div>
        <div class="form-input">
            <label>Password</label>
            <input type="password" placeholder="Password"
                   formControlName="password"
                   minlength="8"
            >
        </div>
        <button type="submit" class="app-login-btn">
            {{ isLoginMode ? 'Login' : 'Sign Up' }}
        </button>
        <p>{{ isLoginMode ? 'Do not have an accout yet ?' : 'Already Have an account' }}
            <button class="app-toggle-btn" (click)="onSwitchMode()">
                {{ isLoginMode ? 'Register Here.' : 'Sign Up Here' }}
            </button>
        </p>
    </form>
</div>
