import {Component, OnInit, inject} from '@angular/core';
import {TaskService} from "../Service/task.service";
import {Task} from "../Model/task";

@Component({
    selector: 'app-dashboard',
    templateUrl: './dashboard.component.html',
    styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {

    showCreateTaskForm: boolean = false;
    isEditTask: boolean = false;
    selectedTask: Task;
    currentTaskId: string;

    task: any;

    errorMessage: any;

    taskService = inject(TaskService);

    isLoading: boolean = false;

    ngOnInit() {
        this.fetchTasks();
    }

    fetchTasks() {
        this.isLoading = true;
        this.taskService.fetchAllTask().subscribe({
            next: (tasks) => {
                console.log('Task Get:', tasks);
                this.task = tasks;
                this.isLoading = false;
            },
            error: (err: any) => {
                console.error('Error occurred:', err);
                this.errorMessage = err.message;
                this.isLoading = false;
            }
        });
    }


    OpenCreateTaskForm() {
        this.showCreateTaskForm = true;
    }

    CloseCreateTaskForm() {
        this.showCreateTaskForm = false;
    }

    CreateOrUpdateTask(event: Task) {
        if (!this.isEditTask) {
            this.taskService.createTask(event);
        } else {
            this.taskService.updateTask(this.currentTaskId, event);
        }
    }

    deleteTask(id: string) {
        this.taskService.deleteTaskById(id);
    }

    deleteAllTask() {
        this.taskService.deleteAllTask();
    }

    editTask(id: string) {
        this.showCreateTaskForm = true;
        this.isEditTask = true;
        this.currentTaskId = id;

        this.selectedTask = this.task.find((task) => {return task?.id === id});
    }
}
<app-create-task
        *ngIf="showCreateTaskForm"
        (CloseForm)="CloseCreateTaskForm()"
        (EmitTaskData)="CreateOrUpdateTask($event)"
        [isEditTask]="isEditTask"
        [selectedTask]="selectedTask"
></app-create-task>

<div class="dashboard-container">
    <div style="display: flex;">
        <div style="width: 50%">
            <h2>Dashboard</h2>
        </div>
        <div style="width: 50%; text-align: right;">
            <button class="create-task-btn" (click)="OpenCreateTaskForm()">Create Task</button>
            <button class="create-task-btn" (click)="fetchTasks()">Fetch Tasks</button>
            <button class="create-task-btn" (click)="deleteAllTask()">Clear Tasks</button>
        </div>
    </div>

    <hr>

    <div class="dashboard-item-container">
        <div class="sb sb-error">Some error message...</div>
        <div class="loader" *ngIf="isLoading"></div>
        <div style="text-align: center" *ngIf="!task?.length">
            <h2>No task has been created yet !</h2>
        </div>
        <ng-container *ngIf="!isLoading">
            <div class="task-item" *ngFor="let task of task"
                 [ngClass]="'task-' + task?.status"
            >
                <div class="task-title">
                    <h3>{{ task?.title }}</h3>
                </div>
                <p><b>Assigned to: </b>{{ task?.assignedTo }}</p>
                <p><b>Priority: </b>{{ task?.priority }}</p>
                <p><b>Status: </b>{{ task?.status }}</p>
                <div class="task-action-icons">
                    <button class="task-action-btn" title="Detail">
                        <i class="fa fa-info-circle" aria-hidden="true"></i>
                    </button>
                    <button class="task-action-btn" title="Edit" (click)="editTask(task?.id)">
                        <i class="fa fa-pencil-square-o" aria-hidden="true"></i>
                    </button>
                    <button class="task-action-btn" title="Delete" (click)="deleteTask(task?.id)">
                        <i class="fa fa-trash" aria-hidden="true"></i>
                    </button>
                </div>
            </div>
        </ng-container>
    </div>
</div>
