import {Component, EventEmitter, inject, Input, OnInit, Output, ViewChild} from '@angular/core';
import {FormBuilder, FormGroup} from "@angular/forms";
import {Task} from "../../Model/task";
import {TaskService} from "../../Service/task.service";

@Component({
    selector: 'app-create-task',
    templateUrl: './create-task.component.html',
    styleUrls: ['./create-task.component.css']
})
export class CreateTaskComponent implements OnInit {

    @Input() isEditTask: boolean = false;

    @Input() selectedTask: Task;

    @Output()
    CloseForm: EventEmitter<boolean> = new EventEmitter<boolean>();

    @Output()
    EmitTaskData: EventEmitter<Task> = new EventEmitter<Task>();

    taskForm: FormGroup = new FormGroup<any>({});

    taskService = inject(TaskService);

    constructor(
        private formBuilder: FormBuilder,
    ) {
    }

    ngOnInit() {
        this.buildForm();
        if (this.isEditTask) {
            this.patchTask();
        }
    }

    buildForm() {
        this.taskForm = this.formBuilder.group({
            title: undefined,
            description: undefined,
            assignedTo: undefined,
            createdAt: undefined,
            priority: undefined,
            status: undefined,
        });
    }

    OnCloseForm() {
        this.CloseForm.emit(false);
    }

    onSubmit() {
        console.log(this.taskForm.value);
        this.EmitTaskData.emit(this.taskForm.value);
        this.OnCloseForm();
    }

    patchTask() {
        this.taskForm.patchValue({
            title: this.selectedTask?.title,
            description: this.selectedTask?.description,
            assignedTo: this.selectedTask?.assignedTo,
            createdAt: this.selectedTask?.createdAt,
            priority: this.selectedTask?.priority,
            status: this.selectedTask?.status,
        })
    }
}
<div class="overlay">
	<div class="popup">
		<h2>{{isEditTask ? 'Edit' : 'Create'}} a new Task</h2>
		<a class="close" (click)="OnCloseForm()">&times;</a>
		<div class="content" >
			<section class="container">
                <form class="form" [formGroup]="taskForm">
                  <div class="input-box">
                    <label>Title*</label>
                    <input type="text" formControlName="title" required />
                  </div>
                  <div class="input-box">
                    <label>Description*</label>
                    <textarea rows="8" formControlName="description" required></textarea>
                  </div>
                  <div class="column">
                    <div class="input-box">
                      <label>Assigned To</label>
                      <input type="text" formControlName="assignedTo" required />
                    </div>
                    <div class="input-box">
                      <label>Created At</label>
                      <input type="date" formControlName="createdAt" required />
                    </div>
                  </div>
                  <div class="input-box address">
                    <div class="column">
                      <div class="select-box">
                        <select formControlName="priority">
                          <option hidden>Priority</option>
                          <option value="low">Low</option>
                          <option value="medium">Medium</option>
                          <option value="high">High</option>
                          <option value="critical">Critical</option>
                        </select>
                      </div>
                      <div class="select-box">
                        <select formControlName="status">
                          <option hidden>Status</option>
                          <option value="open">Open</option>
                          <option value="started">Started</option>
                          <option value="in-progress">In Progress</option>
                          <option value="complete">Complete</option>
                        </select>
                      </div>
                    </div>
                  </div>
                  <button (click)="onSubmit()">{{ isEditTask ? 'Update Task' : 'Create Task' }}</button>
                </form>
              </section>
		</div>
	</div>
</div>
