import {inject, Injectable} from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {map, Observable} from "rxjs";
import {error} from "@angular/compiler-cli/src/transformers/util";
import {Task} from "../Model/task";

@Injectable({
    providedIn: 'root'
})
export class TaskService {

    static API = 'https://task-management-def88-default-rtdb.firebaseio.com/';

    http = inject(HttpClient);


    constructor() {
    }


    protected getApi(): string {
        return TaskService.API;
    }

    createTask(data: Task) {
        this.http.post<{ name: string }>(
            this.getApi() + '/tasks.json',
            data,
            {headers: {'task-create': 'created'}}
        ).subscribe({
            next: (res) => {
                console.log('Task created:', res);
            },
            error: (err: any) => {
                console.error('Error occurred:', err);
            }
        });
    }


    fetchAllTask() {
        this.http.get<{ [key: string]: Task }>(this.getApi() + '/tasks.json')
            .pipe(map((response) => {
                // Transform data
                let tasks = [];
                for (let key in response) {
                    if (response.hasOwnProperty(key)) {
                        tasks.push({...response[key], id: key});
                    }
                }
                return tasks;
            }))
            .subscribe({
                next: (tasks) => {
                    console.log('Task Get:', tasks);
                    return tasks;
                },
                error: (err: any) => {
                    console.error('Error occurred:', err);
                }
            });
    }
}
