import {Component, ViewChild} from '@angular/core';
import {NgForm} from "@angular/forms";

@Component({
    selector: 'app-root',
    templateUrl: './app.component.html',
    styleUrls: ['./app.component.css']
})
export class AppComponent {
    title = 'template-driven-form';

    isSubmitted: boolean = false;

    firstName: string = '';
    lastName: string = '';
    dob: string = '';
    emailAddress: string = '';
    gender: string = '';
    country: string = '';
    city: string = '';
    region: string = '';
    postal: string = '';
    userName: string = '';
    IsAgreed: boolean = false;

    @ViewChild('registrationForm') form: NgForm;

    genders = [
        {id: 'check-male', value: 'Male', display: 'Male'},
        {id: 'check-female', value: 'Female', display: 'Female'},
        {id: 'check-other', value: 'Others', display: 'Others'}
    ]

    onSubmit() {
        this.isSubmitted = true;
        this.firstName = this.form.value.firstname;
        this.lastName = this.form.value.lastname;
        this.emailAddress = this.form.value.email;
        this.country = this.form.value.address.country;
        this.city = this.form.value.address.city;
        this.region = this.form.value.address.region;
        this.postal = this.form.value.address.postal;
        this.userName = this.form.value.username;
        this.dob = this.form.value.dob;
        this.IsAgreed = this.form.value;

        this.form.reset();

        this.form.form.patchValue({
            gender: 'male',
            address: {
                country: 'India'
            }
        });

    }

    GenerateUserName() {
        const firstName = this.form.value.firstname.trim();
        const lastName = this.form.value.lastname.trim();
        const dob = new Date(this.form.value.dob);

        // Validate date of birth
        if (isNaN(dob.getTime())) {
            console.error('Invalid date of birth');
            return;
        }

        let userName = this.createUsername(firstName, lastName, dob);

        userName = userName.toLowerCase();

        console.log('username::', userName);

        this.form.controls['username'].setValue(userName);
    }

    private createUsername(firstName: string, lastName: string, dob: Date): string {
        const firstPart = firstName.length >= 3 ? firstName.slice(0, 3) : firstName;
        const lastPart = lastName.length >= 3 ? lastName.slice(0, 3) : lastName;

        const year = dob.getFullYear();

        return `${firstPart}${lastPart}${year}`;
    }

}
<section class="container" [ngClass]="{'form-shadow': registrationForm.touched}">
    <header>Registration Form</header>

    <form class="form" (ngSubmit)="onSubmit()" #registrationForm="ngForm">
        <div class="column">
            <div class="input-box">
                <input type="text" placeholder="First Name" name="firstname" ngModel required #firstName="ngModel"/>
                <div class="small" *ngIf="firstName.invalid && firstName.touched">
                    <small>* First Name is Required !</small>
                </div>
            </div>

            <div class="input-box">
                <input type="text" placeholder="Last Name" name="lastname" ngModel required #lastName="ngModel"/>
                <div class="small" *ngIf="lastName.invalid && lastName.touched">
                    <small>* Last Name is Required !</small>
                </div>
            </div>
        </div>

        <div class="input-box">
            <input type="text" placeholder="Email" name="email" ngModel email #email="ngModel"/>
            <div class="small" *ngIf="email.invalid && email.touched">
                <small>* Email is Required !</small>
            </div>
        </div>

        <div class="column">
            <div class="input-box">
                <input type="number" placeholder="Phone Number" name="phoneNumber" ngModel/>
            </div>
            <div class="input-box">
                <input type="date" placeholder="Date of Birth" name="dob" ngModel required #dob="ngModel"/>
                <div class="small" *ngIf="dob.invalid && dob.touched">
                    <small>* Dob is required !</small>
                </div>
            </div>
        </div>

        <div class="input-box">
            <input type="text" placeholder="username" name="username" readonly ngModel required #username="ngModel"/>
            <div class="small" *ngIf="username.invalid && username.touched">
                <small>* Username is required !</small>
            </div>
            <button class="btn-gen-username" [disabled]="firstName.invalid || lastName.invalid || dob.invalid"
                    (click)="GenerateUserName()" type="button"
            >
                Create a Username
            </button>
        </div>

        <div class="gender-box">
            <h3>Gender</h3>
            <div class="gender-option">
                <div class="gender" *ngFor="let gender of genders">
                    <input type="radio" [id]="gender?.id" name="gender" ngModel/>
                    <label [for]="gender?.id">{{ gender?.display }}</label>
                </div>
            </div>
        </div>

        <div class="input-box address" ngModelGroup="address" #address="ngModelGroup">
            <label>Address</label>
            <input type="text" placeholder="Street address" name="street1" ngModel required/>
            <input type="text" placeholder="Street address line 2" name="street2" ngModel required/>
            <div class="column">
                <div class="select-box">
                    <select name="country">
                        <option hidden>Country</option>
                        <option>America</option>
                        <option>Japan</option>
                        <option>India</option>
                        <option>Nepal</option>
                    </select>
                </div>
                <input type="text" placeholder="City" name="city" ngModel required/>
            </div>
            <div class="column">
                <input type="text" placeholder="Region" name="region" ngModel required/>
                <input type="number" placeholder="Postal code" name="postalCode" ngModel required/>
            </div>

            <div class="column">
                <input type="checkbox" name="agreement" [ngModel]="IsAgreed">
                <label> I agree to terms & conditions</label><br>
            </div>

            <small *ngIf="address.invalid && address.touched"> * Some of the address is invalid</small>
        </div>
        <input type="submit" value="Submit" class="submit-btn" [disabled]="registrationForm.invalid">
    </form>
</section>

<section class="user-detail-container">
    <div class="user-details-container">
        <div class="user-avatar">
            <p>{{ firstName.slice(0, 1).toUpperCase() }}{{ lastName.slice(0, 1).toUpperCase() }}</p>
        </div>
        <div class="user-details">
            <div class="user-name">
                <h2>{{ firstName }} {{ lastName }}</h2>
            </div>
            <p><b>Email:</b> {{ emailAddress }}</p>
            <p><b>Username:</b> {{ userName }} </p>
            <p><b>Date of birth:</b> {{ dob }}</p>
            <div class="user-name">
                <h3>Address</h3>
            </div>
            <span>{{ country }}</span>.
            <span>{{ city }}</span>.
            <span>{{ region }}</span>.
            <span> {{ postal }}</span>
        </div>
    </div>
</section>
